// Importa a função que você quer testar
const somar = require('./matematica');

// 'describe' agrupa testes relacionados
describe('Testes para a função somar', () => {

  // 'test' ou 'it' define um caso de teste individual
  test('deve somar 2 + 2 e retornar 4', () => {
    // 'expect' é o que você espera que aconteça
    // '.toBe()' é um "matcher" que verifica igualdade estrita (===)
    expect(somar(2, 2)).toBe(4);
  });

  it('deve somar números negativos corretamente', () => {
    expect(somar(-5, -10)).toBe(-15);
  });

  it('deve somar um número positivo e um negativo', () => {
    expect(somar(10, -5)).toBe(5);
  });

});